from sklearn.naive_bayes import GaussianNBimport pandas as pdimport numpy as npimport pickledf = pd.read_csv('EvaluationDataset.csv')xf = df[['keyword', 'grammar', 'qst']];x = np.array(xf.values)yf = df[['class']]y = np.array(yf.values).ravel()classifier = GaussianNB()classifier.fit(x, y)with open('GaussianPrediction.pickle', 'wb') as f:    pickle.dump(classifier, f)def predict(k, g, q):    predicted = classifier.predict([[k, g, q]])    accuracy = classifier.predict_proba([[k, g, q]])    return predicted